import React, { useCallback, useEffect, useState } from "react";
import PropTypes from "prop-types";
import { Col, Row } from "antd";

import styled from "styled-components";

import Skill from "./skill";
import { addLeft, addTop } from "../utils/position";
import { getDataSkillTree } from "../utils/data";
import Line from "./line";
import { STATUS_SKILL } from "../utils/enum";
import { generateNodes } from "../utils/branch";

const RootStyled = styled.div`
  position: absolute;
  top: ${(props) => props.top};
  left: ${(props) => props.left};
`;

const SkillTree = ({ character, visual, points }) => {
  const [data, setData] = useState(null);
  const [currentSkills, setCurrentSkills] = useState([]);
  const [remainingPoints, setRemainingPoints] = useState(points);

  useEffect(() => {
    setData(getDataSkillTree(character));
  }, [character, setData]);

  const handleSelectSkill = (item) => {
    if (!visual) {
      if (item.status === STATUS_SKILL.CAN_BE_SELECTED) {
        setRemainingPoints(remainingPoints - 1);
        item.status = STATUS_SKILL.IS_SELECTED;
      } else if (item.status === STATUS_SKILL.IS_SELECTED) {
        setRemainingPoints(remainingPoints + 1);
        item.status = STATUS_SKILL.CAN_BE_SELECTED;
      }
    }
  };

  const getSkill = (item, top, left, lineColor, skillColor, first) => {
    return (
      <Skill
        item={item}
        firstSkill={first}
        top={top}
        left={left}
        lineColor={lineColor}
        skillColor={skillColor}
        onSelect={handleSelectSkill}
        visual={visual}
      />
    );
  };

  const generateMainBranch = useCallback((
    items,
    lineColor,
    skillColor,
    topInit,
    leftInit
  ) => {
    let top = topInit;
    let left = leftInit;
    let parentPosition = null;
    let first = true;
    let skills = [];

    items.forEach((item) => {
      top += addTop(item.position, parentPosition, item.id);
      left += addLeft(item.position, parentPosition, item.id);
      parentPosition = item.position;

      skills.push(getSkill(item, top, left, lineColor, skillColor, first));
      skills = generateNodes(
        item,
        top + 30,
        left + 15,
        parentPosition,
        skills,
        lineColor,
        skillColor
      );
    });
    return skills;
  }, []);

  const renderSkillTree = useCallback(() => {
    if (data && character) {
      const {
        itemsRoot,
        itemsFirst,
        itemsSecond,
        itemsThird,
        itemsFourth,
        lineColor,
        skillColor,
      } = data;
      let skills = generateMainBranch(itemsFirst, lineColor, skillColor, 0);
      skills = [
        ...generateMainBranch(itemsRoot, lineColor, skillColor, -385, 450),
        ...generateMainBranch(itemsFirst, lineColor, skillColor, 0, 0),
        ...generateMainBranch(itemsSecond, lineColor, skillColor, 0, 450),
        ...generateMainBranch(itemsThird, lineColor, skillColor, 0, 480),
        ...generateMainBranch(itemsFourth, lineColor, skillColor, 0, 900),
      ];
      setCurrentSkills(skills);
    }
  }, [character, data, generateMainBranch]);
  
  useEffect(() => {
    renderSkillTree();
  }, [renderSkillTree]);
  

  return (
    currentSkills &&
    currentSkills.length && (
      <div style={{ margin: "0 auto", width: "1000px" }}>
        <Row>
          <Col
            style={{
              paddingTop: "350px",
            }}
          >
            <RootStyled>
              <Line
                top={-414}
                left={451}
                color={
                  visual ||
                  data?.itemsRoot?.find((item) => item.id === "r-4").isSelect
                    ? data.lineColor
                    : "gray"
                }
                rotation={90}
                translate={"0,0"}
                length={903}
              />
            </RootStyled>
            <div style={{ position: "relative" }} id="container">
              {currentSkills}
            </div>
          </Col>
        </Row>
      </div>
    )
  );
};

SkillTree.propTypes = {
  character: PropTypes.string,
  visual: PropTypes.bool,
  points: PropTypes.number,
};

export default SkillTree;
