import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import { Col, Row } from "antd";

import Skill from "./skill";
import backgroundPaladin from "../asserts/images/paladin/background_paladin.png";

import styled from "styled-components";
import RootSkillTree from "./rootSkillTree";
import { addLeft, addTop } from "../utils/position";
import { getDataSkillTree } from "../utils/data";

const BackgroundImage = styled.div`
  position: absolute;
  background-image: url(${backgroundPaladin});
  background-repeat: no-repeat;
  background-size: cover;
  width: 1700px;
  height: 1800px;
  opacity: 0.6;
  margin-left: -500px;
`;

const SkillTree = ({ character }) => {
  const [data, setData] = useState(null);
  const [container, setContainer] = useState(null);

  useEffect(() => {
    setContainer(document.getElementById("container"));
  }, [container]);

  const renderSkillTree = () => {
    const {
      itemsRoot,
      itemsFirst,
      itemsSecond,
      itemsThird,
      itemsFourth,
      lineColor,
      skillColor,
    } = data;
    let skills = generateMainBranch(itemsFirst, lineColor, skillColor, 0);
    skills = [
      generateMainBranch(itemsRoot, lineColor, skillColor, -385, 450),
      generateMainBranch(itemsFirst, lineColor, skillColor, 0, 0),
      generateMainBranch(itemsSecond, lineColor, skillColor, 0, 450),
      generateMainBranch(itemsThird, lineColor, skillColor, 0, 480),
      generateMainBranch(itemsFourth, lineColor, skillColor, 0, 900),
    ];

    return <>{skills}</>;
  };
  
  useEffect(() => {
    setData(getDataSkillTree(character));
    if (container) {
      renderSkillTree();
    }
  }, [character, setData, renderSkillTree, container]);

  const generateNodes = (
    item,
    top,
    left,
    parentPosition,
    skills,
    lineColor,
    skillColor
  ) => {
    if (item.nodes && item.nodes.length > 0) {
      let topNode = top;
      let leftNode = left;
      let parentPositionNode = parentPosition;
      item.nodes.forEach((item) => {
        topNode += addTop(item.position, parentPositionNode, item.id);
        leftNode += addLeft(item.position, parentPositionNode, item.id);
        skills.push(
          <Skill
            position={item.position}
            top={topNode}
            left={leftNode}
            isSquare={item.isSquare}
            lineColor={lineColor}
            skillColor={skillColor}
            imageSkill={item.image}
            text={item.id}
            parentsLines={item.parentsLines}
          />
        );
        parentPositionNode = item.position;
        generateNodes(
          item,
          topNode,
          leftNode + 30,
          parentPositionNode,
          skills,
          lineColor,
          skillColor
        );
      });
    }
    return skills;
  };

  const generateMainBranch = (
    items,
    lineColor,
    skillColor,
    topInit,
    leftInit
  ) => {
    let top = topInit;
    let left = leftInit;
    let parentPosition = null;
    let first = true;
    let skills = [];

    items.forEach((item) => {
      top += addTop(item.position, parentPosition, item.id);
      left += addLeft(item.position, parentPosition, item.id);
      parentPosition = item.position;

      skills.push(
        <Skill
          firstSkill={first}
          position={item.position}
          top={top}
          left={left}
          isSquare={item.isSquare}
          lineColor={lineColor}
          skillColor={skillColor}
          withoutLine={item.withoutLine}
          imageSkill={item.image}
          text={item.id}
          parentsLines={item.parentsLines}
        />
      );
      skills = generateNodes(
        item,
        top + 30,
        left + 15,
        parentPosition,
        skills,
        lineColor,
        skillColor
      );
    });
    return skills;
  };

  return (
    data && (
      <>
        <BackgroundImage></BackgroundImage>
        <Row>
          <Col
            style={{
              paddingTop: "350px",
              paddingLeft: "50px",
            }}
          >
            <RootSkillTree
              skillColor={data.skillColor}
              lineColor={data.lineColor}
            />
            <div style={{ position: "relative" }} id="container">
              {renderSkillTree()}
            </div>
          </Col>
        </Row>
      </>
    )
  );
};

SkillTree.propTypes = {
  character: PropTypes.string,
};

export default SkillTree;
