import React from "react";
import styled from "styled-components";
import PropTypes from "prop-types";
import { Avatar, Col, Divider, List, Row } from "antd";
import InfiniteScroll from "react-infinite-scroll-component";

const BlocStyled = styled.div`
  position: absolute;
  top: 30px;
  left: -400px;
  background-color: white;
  width: 500px;
  height: 200px;
  font-size: 25px;
  border-radius: 5px;
`;

const DivLevel = styled.div`
  font-size: 50px;
  text-align: center;
  margin: 20px;
`;

const DivPoints = styled.div`
  font-size: 20px;
  text-align: center;
  margin: 2px;
`;

const StyledImage = styled.img`
  position: relative;
  width: ${(props) => (props.isSquare ? "10" : "35")}px;
  height: ${(props) => (props.isSquare ? "10" : "35")}px;
  margin-top: -${(props) => (props.isSquare ? "8" : "10")}px;
  margin-left: -${(props) => (props.isSquare ? "6" : "8")}px;
  border: 2px solid ${(props) => props.color};
  border-radius: ${(props) => (props.isSquare ? "0" : "50")}px;
`;

const DashBoard = ({
  remainingPoints,
  selectedSkills,
  level,
  simulationInProgress,
}) => {
  return (
    simulationInProgress && (
      <BlocStyled>
        <Row>
          <Col span={10}>
            <DivLevel>{`Niv. ${level}`}</DivLevel>
            <Divider style={{ margin: 0 }} />
            <DivPoints>
              <span style={{ fontWeight: 600 }}>{remainingPoints}</span> points
              restantes à dépenser
            </DivPoints>
          </Col>
          <Col>
            <div style={{ overflow: "auto", width: "288px", height: "97px" }}>
              <ul>
                {selectedSkills.map(
                  (item) =>
                    !item.isSquare && (
                      <>
                        <StyledImage
                          color={"orange"}
                          isSquare={item.isSquare}
                          src={require(`../../asserts/images/paladin/${item.image}.jpg`)}
                        />
                        <li>{item.text}</li>
                      </>
                    )
                )}
              </ul>
            </div>
            <Divider style={{ margin: 0 }} />

            <div style={{ overflow: "auto", width: "288px", height: "97px" }}>
              <div
                id="scrollableDiv"
                style={{
                  height: 400,
                  overflow: "auto",
                  padding: "0 16px",
                  border: "1px solid rgba(140, 140, 140, 0.35)",
                }}
              >
                <InfiniteScroll
                  dataLength={
                    selectedSkills.filter((item) => item.isSquare).length
                  }
                  hasMore={false}
                  scrollableTarget="scrollableDiv"
                >
                  <List
                    dataSource={selectedSkills.filter((item) => item.isSquare)}
                    renderItem={(item) => (
                      <List.Item key={item.id}>
                        <List.Item.Meta
                          avatar={
                            <Avatar
                              src={require(`../../asserts/images/paladin/${item.image}.jpg`)}
                            />
                          }
                          title={item.text}
                        />
                      </List.Item>
                    )}
                  />
                </InfiniteScroll>
              </div>
            </div>
          </Col>
        </Row>
      </BlocStyled>
    )
  );
};

DashBoard.propTypes = {
  remainingPoints: PropTypes.number,
  selectedSkills: PropTypes.array,
  level: PropTypes.number,
  simulationInProgress: PropTypes.bool,
};

export default DashBoard;
