import React, { useState } from "react";
import PropTypes from "prop-types";
import styled from "styled-components";

const Line = styled.span`
  border-left: 3px solid #000;
  display: inline-block;
  height: 90px;
  margin: 0 20px;
  margin-top: -30px;
  transform: rotate(${(props) => props.deg}deg);
`;

const StyledImage = styled.img`
  position: relative;
  width: 50px;
  height: 50px;
  margin-top: -8px;
  margin-left: -5px;
  border: 3px solid blue;
  opacity: 0.5;
`;

export const ALIGN_SKILL = {
  LEFT: "left",
  LEFT_BOTTOM: "left-bottom",
  MIDDLE: "middle",
  RIGHT: "right",
  RIGHT_BOTTOM: "left-bottom",
};

export const ROTATE_LINE = {
  LEFT: 90,
  LEFT_BOTTOM: 45,
  MIDDLE: 0,
  RIGHT_BOTTOM: -45,
  RIGHT: 90,
};

const Skill = () => {
  const [rotation, setRotation] = useState()
  return (
    <>
      <div
        style={{
          width: "50px",
          height: "auto",
          marginLeft: "5px",
        }}
      >
        <Line deg={ROTATE_LINE.LEFT_BOTTOM} />
        <StyledImage
          src={require("../../asserts/data/images/Paladin/ability_paladin_speedoflight.jpg")}
        />
      </div>
      <div
        style={{
          width: "50px",
          height: "auto",
          marginLeft: "5px",
        }}
      >
        <Line></Line>
        <StyledImage
          src={require("../../asserts/data/images/Paladin/ability_paladin_speedoflight.jpg")}
        />
      </div>
    </>
  );
};

Skill.propTypes = {
  rotation: PropTypes.string,
};

export default Skill;
