import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import { Col, Row } from "antd";

import Skill, { POSITION_SKILL } from "./skill";
import paladin from "../asserts/data/paladin.json";
import { CHARACTER_CLASS } from "../utils/enum";
import chat from '../asserts/images/paladin/background_paladin.png'
const SkillTree = ({ character }) => {
  const [data, setData] = useState(paladin);
  const [container, setContainer] = useState(null);

  const getDataSkillTree = () => {
    switch (character) {
      case CHARACTER_CLASS.PALADIN: {
        setData(paladin);
        break;
      }
      default:
      case POSITION_SKILL.MIDDLE: {
        setData(paladin);
        break;
      }
    }
  };

  useEffect(() => {
    setContainer(document.getElementById("container"));
  }, [container]);

  useEffect(() => {
    getDataSkillTree();
    if (container) {
      renderSkillTree();
    }
  });

  const addTop = (position, parentPosition) => {
    switch (position) {
      case POSITION_SKILL.MIDDLE:
        if (POSITION_SKILL.MIDDLE === parentPosition) return 90;
        if (POSITION_SKILL.LEFT_BOTTOM === parentPosition) return 60;
        if (POSITION_SKILL.RIGHT_BOTTOM === parentPosition) return 50;
        if (POSITION_SKILL.LEFT === parentPosition) return 30;
        if (POSITION_SKILL.RIGHT === parentPosition) return 50;
        return 0;
      case POSITION_SKILL.LEFT_BOTTOM:
        if (POSITION_SKILL.MIDDLE === parentPosition) return 120;
        if (POSITION_SKILL.LEFT_BOTTOM === parentPosition) return 90;
        if (POSITION_SKILL.RIGHT_BOTTOM === parentPosition) return 80;
        if (POSITION_SKILL.LEFT === parentPosition) return 60;
        if (POSITION_SKILL.RIGHT === parentPosition) return 70;
        return 0;
      case POSITION_SKILL.RIGHT_BOTTOM:
        if (POSITION_SKILL.MIDDLE === parentPosition) return 130;
        if (POSITION_SKILL.LEFT_BOTTOM === parentPosition) return 100;
        if (POSITION_SKILL.RIGHT_BOTTOM === parentPosition) return 90;
        if (POSITION_SKILL.LEFT === parentPosition) return 70;
        if (POSITION_SKILL.RIGHT === parentPosition) return 80;
        return 0;
      case POSITION_SKILL.LEFT:
        if (POSITION_SKILL.MIDDLE === parentPosition) return 70;
        if (POSITION_SKILL.LEFT_BOTTOM === parentPosition) return 40;
        if (POSITION_SKILL.RIGHT_BOTTOM === parentPosition) return 30;
        if (POSITION_SKILL.LEFT === parentPosition) return 0;
        return 0;
      case POSITION_SKILL.RIGHT:
        if (POSITION_SKILL.MIDDLE === parentPosition) return 50;
        if (POSITION_SKILL.LEFT_BOTTOM === parentPosition) return 20;
        if (POSITION_SKILL.RIGHT_BOTTOM === parentPosition) return 10;
        if (POSITION_SKILL.RIGHT === parentPosition) return 0;
        return 0;
      default:
        return 0;
    }
  };

  const addLeft = (position, parentPosition, id) => {
    switch (position) {
      case POSITION_SKILL.MIDDLE:
        if (POSITION_SKILL.MIDDLE === parentPosition) return 0;
        if (POSITION_SKILL.LEFT_BOTTOM === parentPosition) return 36;
        if (POSITION_SKILL.RIGHT_BOTTOM === parentPosition) return -60;
        if (POSITION_SKILL.LEFT === parentPosition) return 50;
        if (POSITION_SKILL.RIGHT === parentPosition) return -50;
        return 0;
      case POSITION_SKILL.LEFT_BOTTOM:
        if (POSITION_SKILL.MIDDLE === parentPosition) return 20;
        if (POSITION_SKILL.LEFT_BOTTOM === parentPosition) return 55;
        if (POSITION_SKILL.RIGHT_BOTTOM === parentPosition) return -40;
        if (POSITION_SKILL.LEFT === parentPosition) return 80;
        if (POSITION_SKILL.RIGHT === parentPosition) return -30;
        return 0;
      case POSITION_SKILL.RIGHT_BOTTOM:
        if (POSITION_SKILL.MIDDLE === parentPosition) return 0;
        if (POSITION_SKILL.LEFT_BOTTOM === parentPosition) return 40;
        if (POSITION_SKILL.RIGHT_BOTTOM === parentPosition) return -60;
        if (POSITION_SKILL.LEFT === parentPosition) return 40;
        if (POSITION_SKILL.RIGHT === parentPosition) return -50;
        return 0;
      case POSITION_SKILL.LEFT:
        if (POSITION_SKILL.MIDDLE === parentPosition) return 70;
        if (POSITION_SKILL.LEFT_BOTTOM === parentPosition) return 100;
        if (POSITION_SKILL.RIGHT_BOTTOM === parentPosition) return 10;
        if (POSITION_SKILL.LEFT === parentPosition) return 115;
        return 0;
      case POSITION_SKILL.RIGHT:
        if (POSITION_SKILL.MIDDLE === parentPosition) return -60;
        if (POSITION_SKILL.LEFT_BOTTOM === parentPosition) return -30;
        if (POSITION_SKILL.RIGHT_BOTTOM === parentPosition) return -120;
        if (POSITION_SKILL.RIGHT === parentPosition) return -110;
        return 0;
      default:
        return 0;
    }
  };

  const renderSkillTree = () => {
    const { items, lineColor, skillColor } = data;
    console.log(lineColor, skillColor);
    const skills = [];
    let top = 0;
    let left = 0;
    let parentPosition = null;
    let first = true;
    items.forEach((item) => {
      top += addTop(item.position, parentPosition, item.id);
      left += addLeft(item.position, parentPosition, item.id);
      parentPosition = item.position;

      skills.push(
        <Skill
          firstSkill={first}
          position={item.position}
          top={top}
          left={left}
          isSquare={item.isSquare}
          lineColor={lineColor}
          skillColor={skillColor}
        />
      );
      if (item.nodes && item.nodes.length > 0) {
        let topNode = top;
        let leftNode = left;
        let parentPositionNode = parentPosition;
        item.nodes.forEach((item) => {
          topNode += addTop(item.position, parentPositionNode, item.id);
          leftNode += addLeft(item.position, parentPositionNode, item.id);
          skills.push(
            <Skill
              position={item.position}
              top={topNode}
              left={leftNode}
              isSquare={item.isSquare}
              lineColor={lineColor}
              skillColor={skillColor}
            />
          );
          parentPositionNode = item.position;
        });
        first = false;
      }
    });

    return <>{skills}</>;
  };

  return (
    data && (
      <div style={{ backgroundImage: 'url("../asserts/images/paladin/background_paladin.png")' }}>
        <Row>
          <Col
            style={{
              width: "100%",
              height: "1000px",
              paddingLeft: "150px",
            }}
          >
            <div style={{ position: "relative" }} id="container">
              {renderSkillTree()}
            </div>
          </Col>
        </Row>
      </div>
    )
  );
};

SkillTree.propTypes = {
  character: PropTypes.string,
};

export default SkillTree;
