import { CHARACTER_CLASS } from "./enum";
import paladin from "../asserts/data/paladin.json";

export const getDataSkillTree = (character) => {
  switch (character) {
    case CHARACTER_CLASS.PALADIN: {
      return paladin;
    }
    default:
    case CHARACTER_CLASS.GUERRIER: {
      return paladin;
    }
  }
};

export const replaceSkill = (item, skills) => {
  var index = skills.indexOf(
    skills.find((skill) => skill.props.item.id === item.id)
  );
  console.log(skills[index].skill.props)
  if (index !== -1) {
    skills[index] = item;
  }
  return skills
};
