import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import { Col, Row } from "antd";

import Skill, { POSITION_SKILL } from "./skill";
import paladin from "../asserts/data/paladin.json";
import { CHARACTER_CLASS } from "../utils/enum";

const SkillTree = ({ character }) => {
  const [data, setData] = useState(paladin);
  const [container, setContainer] = useState(null);

  const getDataSkillTree = () => {
    switch (character) {
      case CHARACTER_CLASS.PALADIN: {
        setData(paladin);
        break;
      }
      default:
      case POSITION_SKILL.MIDDLE: {
        setData(paladin);
        break;
      }
    }
  };

  useEffect(() => {
    setContainer(document.getElementById("container"));
  }, [container]);

  useEffect(() => {
    getDataSkillTree();
  });

  const renderSkillTree = () => {
    //console.log(document, divParent);
    const { items } = data;
    console.log(container);
    for (let i = 0; i < items.length; i++) {
      const item = items[i];
      console.log(item);
      container.appendChild(
        <Skill id="skill-1-1" position={item.position} top={0} left={0} />
      );
    }
  };

  return (
    <Row>
      <Col
        style={{
          backgroundColor: "yellow",
          width: "500px",
          height: "500px",
          paddingLeft: "150px",
        }}
      >
        <div style={{ position: "relative" }} id="container">
          {/*<Skill
            id="skill-1-1"
            position={POSITION_SKILL.MIDDLE}
            top={0}
            left={0}
          />
          <Skill
            id="skill-1-2"
            position={POSITION_SKILL.RIGHT_BOTTOM}
            parentId={"skill-1-1"}
            top={120}
            left={0}
          />
          <Skill
            id={"skill-1-3"}
            position={POSITION_SKILL.MIDDLE}
            parentId={"skill-1-2"}
            top={200}
            left={-60}
      />*/}
        </div>
      </Col>
    </Row>
  );
};

SkillTree.propTypes = {
  character: PropTypes.string,
};

export default SkillTree;
