import React from "react";
import styled from "styled-components";
import PropTypes from "prop-types";
import { Col, Divider, Row } from "antd";

const BlocStyled = styled.div`
  position: absolute;
  top: 30px;
  left: -400px;
  background-color: white;
  width: 500px;
  height: 200px;
  font-size: 25px;
  border-radius: 5px;
`;

const DivLevel = styled.div`
  font-size: 50px;
  text-align: center;
`;

const DashBoard = ({
  remainingPoints,
  selectedSkills,
  level,
  simulationInProgress,
}) => {
  return (
    simulationInProgress && (
      <BlocStyled>
        <Row align={"middle"}>
          <Col span={10}>
            <DivLevel>{`Niv. ${level}`}</DivLevel>
            <Divider style={{ margin: 0 }} />
            <div>{`Points restantes ${remainingPoints}`}</div>
          </Col>
          <Col>
            <div style={{ overflow: "auto", width: "288px", height: "195px" }}>
              <ul>
                {selectedSkills.map((item) => (
                  <li>{item.text}</li>
                ))}
              </ul>
            </div>
          </Col>
        </Row>
      </BlocStyled>
    )
  );
};

DashBoard.propTypes = {
  remainingPoints: PropTypes.number,
  selectedSkills: PropTypes.array,
  level: PropTypes.number,
  simulationInProgress: PropTypes.bool,
};

export default DashBoard;
