import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import styled from "styled-components";

const Line = styled.span`
  border-left: 3px solid #000;
  display: inline-block;
  height: 90px;
  margin: 0 20px;
  margin-top: -30px;
  transform: rotate(${(props) => props.deg}deg);
`;

const StyledImage = styled.img`
  position: relative;
  width: 50px;
  height: 50px;
  margin-top: -8px;
  margin-left: -5px;
  border: 3px solid blue;
  opacity: 0.5;
`;

export const POSITION_SKILL = {
  LEFT: "left",
  LEFT_BOTTOM: "left-bottom",
  MIDDLE: "middle",
  RIGHT: "right",
  RIGHT_BOTTOM: "left-bottom",
};

export const ALIGN_SKILL = {
  LEFT: "left",
  LEFT_BOTTOM: "left-bottom",
  MIDDLE: "middle",
  RIGHT: "right",
  RIGHT_BOTTOM: "left-bottom",
};

const ROTATE_LINE = {
  LEFT: 90,
  LEFT_BOTTOM: -45,
  MIDDLE: 0,
  RIGHT_BOTTOM: 45,
  RIGHT: 90,
};

const TRANSLATE_SKILL = {
  LEFT: "0,0",
  LEFT_BOTTOM: "0,0",
  MIDDLE: "0,0",
  RIGHT_BOTTOM: "0,0",
  RIGHT: "0,0",
};

const Skill = ({ position }) => {
  useEffect(() => {
    switch (position) {
      case POSITION_SKILL.LEFT: {
        setRotation(ROTATE_LINE.LEFT);
        setRotation(ROTATE_LINE.LEFT);
        break;
      }
      case POSITION_SKILL.LEFT_BOTTOM: {
        setRotation(ROTATE_LINE.LEFT_BOTTOM);
        setRotation(ROTATE_LINE.LEFT_BOTTOM);
        break;
      }
      case POSITION_SKILL.MIDDLE: {
        setRotation(ROTATE_LINE.MIDDLE);
        setRotation(ROTATE_LINE.MIDDLE);
        break;
      }
      case POSITION_SKILL.RIGHT: {
        setRotation(ROTATE_LINE.RIGHT);
        setRotation(ROTATE_LINE.RIGHT);
        break;
      }
      case POSITION_SKILL.RIGHT_BOTTOM: {
        setRotation(ROTATE_LINE.RIGHT_BOTTOM);
        setRotation(ROTATE_LINE.RIGHT_BOTTOM);
        break;
      }
      default:
    }
  }, [position]);
  const [rotation, setRotation] = useState(ROTATE_LINE.MIDDLE);
  const [translate, setTranslate] = useState();
  return (
    <>
      <div
        style={{
          width: "50px",
          height: "auto",
          marginLeft: "5px",
        }}
      >
        <Line deg={rotation} />
        <StyledImage
          src={require("../../asserts/data/images/Paladin/ability_paladin_speedoflight.jpg")}
        />
      </div>
    </>
  );
};

Skill.propTypes = {
  position: PropTypes.string,
};

export default Skill;
