import { Button, Divider, Row } from "antd";
import { CHARACTER_CLASS } from "../utils/enum";
import { useState } from "react";

import SkillTree from "../skillTree";
import styled from "styled-components";
import backgroundPaladin from "../asserts/images/paladin/background_paladin.png";

const SectionSkillTree = styled.section`
  position: absolute;
  background-image: url(${backgroundPaladin});
  background-repeat: no-repeat;
  background-size: cover;
  width: 100%;
  height: 1800px;
  opacity: 0.6;
  margin-left: -460px;
`;

const Menu = () => {
  const [character, setCharacter] = useState(null);
  const handleClick = (classCharacter) => {
    setCharacter(classCharacter);
  };

  return (
    <div style={{ backgroundColor: "#272a35" }}>
      <section>
        <Row align={"center"}>
          <Button onClick={() => handleClick(CHARACTER_CLASS.GUERRIER)}>
            Guerrier
          </Button>
          <Button onClick={() => handleClick(CHARACTER_CLASS.PALADIN)}>
            Paladin
          </Button>
          <Divider />
        </Row>
      </section>
      <section id="container-skill-tree" style={{ width: "100%" }}>
        {character && <SkillTree />}
      </section>
    </div>
  );
};

export default Menu;
