import { Button, Divider, Row } from "antd";
import { CHARACTER_CLASS } from "../utils/enum";
import { useState } from "react";

import SkillTree from "../skillTree";

const Menu = () => {
  const [character, setCharacter] = useState(null);
  const handleClick = (classCharacter) => {
    setCharacter(classCharacter);
  };

  return (
    <div>
      <Row align={"center"}>
        <Button onClick={() => handleClick(CHARACTER_CLASS.GUERRIER)}>
          Guerrier
        </Button>
        <Button onClick={() => handleClick(CHARACTER_CLASS.PALADIN)}>
          Paladin
        </Button>
        <Divider />
      </Row>
      <div>
      {character && <SkillTree />}

      </div>
    </div>
  );
};

export default Menu;
