import React from "react";
import styled from "styled-components";
import PropTypes from "prop-types";
import { Avatar, Button, Col, Divider, List, Row, Tooltip } from "antd";
import InfiniteScroll from "react-infinite-scroll-component";
import { CopyOutlined, PaperClipOutlined } from "@ant-design/icons";

const BlocStyled = styled.div`
  position: absolute;
  top: 30px;
  left: -400px;
  background-color: white;
  width: 500px;
  height: 200px;
  font-size: 25px;
  border-radius: 5px;
`;

const DivLevel = styled.div`
  font-size: 50px;
  text-align: center;
  margin: 20px;
`;

const DivPoints = styled.div`
  font-size: 20px;
  text-align: center;
  margin: 2px;
`;

const StyledImage = styled.img`
  position: relative;
  width: 35px;
  height: 35px;
  margin: 2px;
  border: 2px solid ${(props) => props.color};
  border-radius: ${(props) => (props.isSquare ? "0" : "50")}px;
`;

const DashBoard = ({
  remainingPoints,
  selectedSkills,
  level,
  simulationInProgress,
  skillColor,
}) => {
  return (
    simulationInProgress && (
      <BlocStyled>
        <Row>
          <Col span={10}>
            <DivLevel>{`Niv. ${level}`}</DivLevel>
            <Divider style={{ margin: 0 }} />
            <DivPoints>
              <span style={{ fontWeight: 600 }}>{remainingPoints}</span> points
              restantes à dépenser
            </DivPoints>
          </Col>
          <Button
          posi
            icon={<CopyOutlined />}
            disabled={selectedSkills.length === 0}
          />
          <Col>
            <div style={{ overflow: "auto", width: "288px", height: "97px" }}>
              {selectedSkills.map(
                (item) =>
                  item.isSquare && (
                    <Tooltip title={item.text} placement="bottom">
                      <StyledImage
                        color={skillColor}
                        isSquare={item.isSquare}
                        src={require(`../../asserts/images/paladin/${item.image}.jpg`)}
                      />
                    </Tooltip>
                  )
              )}
            </div>
            <Divider style={{ margin: 0 }} />
            <div style={{ overflow: "auto", width: "288px", height: "97px" }}>
              {selectedSkills.map(
                (item) =>
                  !item.isSquare && (
                    <Tooltip title={item.text} placement="bottom">
                      <StyledImage
                        color={skillColor}
                        isSquare={item.isSquare}
                        src={require(`../../asserts/images/paladin/${item.image}.jpg`)}
                      />
                    </Tooltip>
                  )
              )}
            </div>
          </Col>
        </Row>
      </BlocStyled>
    )
  );
};

DashBoard.propTypes = {
  remainingPoints: PropTypes.number,
  selectedSkills: PropTypes.array,
  level: PropTypes.number,
  simulationInProgress: PropTypes.bool,
  skillColor: PropTypes.string,
};

export default DashBoard;
