import React from "react";
import styled from "styled-components";
import PropTypes from "prop-types";
import { Col, Divider, Row } from "antd";

const BlocStyled = styled.div`
  position: absolute;
  top: 30px;
  left: -400px;
  background-color: white;
  width: 500px;
  height: 200px;
  font-size: 25px;
  border-radius: 5px;
`;

const DivLevel = styled.div`
  font-size: 50px;
  text-align: center;
  margin: 20px;
`;

const DivPoints = styled.div`
  font-size: 20px;
  text-align: center;
  margin: 2px;
`;

const StyledImage = styled.img`
  &:hover {
    box-shadow: 0px 0px 9px white;
    transition: all 0.3s ease-out;
  }
  position: relative;
  width: ${(props) => (props.isSquare ? "50" : "55")}px;
  height: ${(props) => (props.isSquare ? "50" : "55")}px;
  margin-top: -${(props) => (props.isSquare ? "8" : "10")}px;
  margin-left: -${(props) => (props.isSquare ? "6" : "8")}px;
  border: 3px solid ${(props) => props.color};
  transform: ${(props) => props.transform};
  z-index: 5;
  filter: grayscale(${(props) => (props.grayscale ? 0 : 1)});
  border-radius: ${(props) => (props.isSquare ? "0" : "50")}px;
`;

const DashBoard = ({
  remainingPoints,
  selectedSkills,
  level,
  simulationInProgress,
}) => {
  return (
    simulationInProgress && (
      <BlocStyled>
        <Row>
          <Col span={10}>
            <DivLevel>{`Niv. ${level}`}</DivLevel>
            <Divider style={{ margin: 0 }} />
            <DivPoints>
              <span style={{ fontWeight: 600 }}>{remainingPoints}</span> points
              restantes à dépenser
            </DivPoints>
          </Col>
          <Col>
            <div style={{ overflow: "auto", width: "288px", height: "97px" }}>
              <ul>
                {selectedSkills.map(
                  (item) =>
                    !item.isSquare && (
                      <>
                       <StyledImage
              color={currentSkillColor}
              transform={transformSkill}
              isSquare={item.isSquare}
              src={require(`../../asserts/images/paladin/${item.image}.jpg`)}
              grayscale={
                visual || item.status !== STATUS_SKILL.CANNOT_BE_SELECTED
              }
            />
                        <li>{item.text}</li>
                      </>
                    )
                )}
              </ul>
            </div>
            <Divider style={{ margin: 0 }} />

            <div style={{ overflow: "auto", width: "288px", height: "97px" }}>
              <ul>
                {selectedSkills.map(
                  (item) => item.isSquare && <li>{item.text}</li>
                )}
              </ul>
            </div>
          </Col>
        </Row>
      </BlocStyled>
    )
  );
};

DashBoard.propTypes = {
  remainingPoints: PropTypes.number,
  selectedSkills: PropTypes.array,
  level: PropTypes.number,
  simulationInProgress: PropTypes.bool,
};

export default DashBoard;
