import { Button, Col, Divider, Form, InputNumber, Row, Switch } from "antd";
import { CHARACTER_CLASS } from "../utils/enum";
import { useState } from "react";

import SkillTree from "../skillTree";
import styled from "styled-components";
import backgroundPaladin from "../asserts/images/paladin/background_paladin_filter.png";
import { getDataSkillTree } from "../utils/data";

const SectionSkillTree = styled.section`
  background-image: url(${backgroundPaladin});
  background-repeat: no-repeat;
  background-size: cover;
  width: 100%;
  height: 1800px;
  opacity: 1;
`;

const Menu = () => {
  const [character, setCharacter] = useState(null);
  const [points, setPoints] = useState(null);
  const [pointsToChoose, setPointsToChoose] = useState(null);
  const [visual, setVisual] = useState(true);
  const [data, setData] = useState(null);
  const [simulationInProgress, setSimulationInProgress] = useState(false);

  const handleClick = (classCharacter) => {
    setCharacter(classCharacter);
    setData(getDataSkillTree(character));
  };

  const handleStart = () => {
    setSimulationInProgress(true);
    setPoints(pointsToChoose);
  };

  const handleReset = () => {
    setPoints(1);
    setSimulationInProgress(false);
  };
  return (
    <div style={{ backgroundColor: "#272a35" }}>
      <section>
        <Row align={"center"}>
          <Button onClick={() => handleClick(CHARACTER_CLASS.GUERRIER)}>
            Guerrier
          </Button>
          <Button onClick={() => handleClick(CHARACTER_CLASS.PALADIN)}>
            Paladin
          </Button>
          <Divider />
        </Row>
      </section>
      {character && data && (
        <>
          <section
            style={{ backgroundColor: data.primaryColor, padding: "10px" }}
          >
            <Form style={{ height: "35px" }}>
              <Row justify={"start"}>
                {!simulationInProgress ? (
                  <Col span={4}>
                    <Form.Item>
                      <Button onClick={handleStart}>
                        Commencer la simulation
                      </Button>
                    </Form.Item>
                  </Col>
                ) : (
                  <Col span={4}>
                    <Form.Item>
                      <Button onClick={handleReset}>
                        RÃ©initialiser l'arbre
                      </Button>
                    </Form.Item>
                  </Col>
                )}
              </Row>
            </Form>
          </section>
          <SectionSkillTree id="container-skill-tree" style={{ width: "100%" }}>
            <SkillTree
              points={points}
              character={character}
              data={data}
              visual={visual}
              simulationInProgress={simulationInProgress}
            />
          </SectionSkillTree>
        </>
      )}
    </div>
  );
};

export default Menu;
