import { Button, Col, Divider, Form, InputNumber, Row, Switch } from "antd";
import { CHARACTER_CLASS } from "../utils/enum";
import { useState } from "react";

import SkillTree from "../skillTree";
import styled from "styled-components";
import backgroundPaladin from "../asserts/images/paladin/background_paladin_filter.png";
import { getDataSkillTree } from "../utils/data";

const SectionSkillTree = styled.section`
  background-image: url(${backgroundPaladin});
  background-repeat: no-repeat;
  background-size: cover;
  width: 100%;
  height: 1800px;
  opacity: 1;
`;

const Menu = () => {
  const [character, setCharacter] = useState(null);
  const [points, setPoints] = useState(1);
  const [visual, setVisual] = useState(false);
  const [data, setData] = useState(null);
  const [simulationInProgress, setSimulationInProgress] = useState(false)

  const handleClick = (classCharacter) => {
    setCharacter(classCharacter);
  };

  const handleChange = (value) => {
    setPoints(value);
  };

  const handleChecked = (value) => {
    setVisual(value);
  };

  const handleStart = () => {
    setData(getDataSkillTree(character));
  }

  const handleReset = () => {
    set
  }
  return (
    <div style={{ backgroundColor: "#272a35" }}>
      <section>
        <Row align={"center"}>
          <Button onClick={() => handleClick(CHARACTER_CLASS.GUERRIER)}>
            Guerrier
          </Button>
          <Button onClick={() => handleClick(CHARACTER_CLASS.PALADIN)}>
            Paladin
          </Button>
          <Divider />
        </Row>
      </section>
      {character && data && (
        <>
          <section style={{ backgroundColor: "orange", padding: "10px" }}>
            <Form>
              <Row justify={"start"}>
                <Col span={4}>
                  <Form.Item
                    style={{ margin: "0" }}
                    label="Nombre de points de talents"
                  >
                    <InputNumber
                      defaultValue={points}
                      placeholder="Points"
                      onChange={handleChange}
                      disabled={visual}
                    ></InputNumber>
                  </Form.Item>
                </Col>
                <Col>
                  <Form.Item>
                    <Button onClick={handleStart}>Commencer la simulation</Button>
                  </Form.Item>
                </Col>
                <Col>
                  <Form.Item>
                    <Button>RÃ©initialiser l'arbre</Button>
                  </Form.Item>
                </Col>
                <Col span={2}>
                  <Form.Item style={{ margin: "0" }} label="Mode Visuel">
                    <Switch checked={visual} onChange={handleChecked} />
                  </Form.Item>
                </Col>
              </Row>
            </Form>
          </section>
          <SectionSkillTree id="container-skill-tree" style={{ width: "100%" }}>
            <SkillTree
              points={points}
              character={character}
              data={data}
              visual={visual}
            />
          </SectionSkillTree>
        </>
      )}
    </div>
  );
};

export default Menu;
