import React, { useState } from "react";
import PropTypes from "prop-types";
import { Col, Row } from "antd";

import Skill, { POSITION_SKILL } from "./skill";
import paladin from "../asserts/data/paladin.json";

const SkillTree = ({ character }) => {
  const [data, setData] = useState(null);

  const getDataSkillTree = () => {
    switch (character) {
      case POSITION_SKILL.LEFT: {
        setData(paladin);
        break;
      }
      default:
      case POSITION_SKILL.MIDDLE: {
        setData(paladin);
        break;
      }
    }
  };

  const renderSkillTree = () => {
    getDataSkillTree();
    let divParent = document.getElementById("parent");
    const { items } = data;
    return items.foreach((item) => {
      divParent.appendChild(
        <Skill id="skill-1-1" position={item.position} top={0} left={0} />
      );
    });
  };

    return (
    <Row>
      <Col
        style={{
          backgroundColor: "yellow",
          width: "500px",
          height: "500px",
          paddingLeft: "150px",
        }}
      >
        <div style={{ position: "relative" }} id="parent">
         {/*<Skill
            id="skill-1-1"
            position={POSITION_SKILL.MIDDLE}
            top={0}
            left={0}
          />
          <Skill
            id="skill-1-2"
            position={POSITION_SKILL.RIGHT_BOTTOM}
            parentId={"skill-1-1"}
            top={120}
            left={0}
          />
          <Skill
            id={"skill-1-3"}
            position={POSITION_SKILL.MIDDLE}
            parentId={"skill-1-2"}
            top={200}
            left={-60}
      />*/}
        </div>
      </Col>
    </Row>
  );
};

SkillTree.propTypes = {
  character: PropTypes.string,
};

export default SkillTree;
