import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import styled from "styled-components";

const Line = styled.span`
  border-left: 3px solid #000;
  display: inline-block;
  height: 90px;
  margin: 0 20px;
  margin-top: -30px;
  transform: ${(props) => props.transform};
`;

const StyledImage = styled.img`
  position: relative;
  width: 50px;
  height: 50px;
  margin-top: -8px;
  margin-left: -5px;
  border: 3px solid blue;
  opacity: 0.5;
  transform: ${(props) => props.transform};
`;

const SkillStyled = styled.div`
  width: 50px;
  height: auto;
  position: absolute;
`;

export const POSITION_SKILL = {
  LEFT: "left",
  LEFT_BOTTOM: "left-bottom",
  MIDDLE: "middle",
  RIGHT: "right",
  RIGHT_BOTTOM: "right-bottom",
};

export const ALIGN_SKILL = {
  LEFT: "left",
  LEFT_BOTTOM: "left-bottom",
  MIDDLE: "middle",
  RIGHT: "right",
  RIGHT_BOTTOM: "right-bottom",
};

const TRANSFORM_LINE = {
  LEFT: "rotate(90deg) translate(0,0)",
  LEFT_BOTTOM: "rotate(-45deg) translate(0,0)",
  MIDDLE: "rotate(0deg) translate(0,0)",
  RIGHT_BOTTOM: "rotate(45deg) translate(-30px,10px)",
  RIGHT: "rotate(90deg) translate(0,0)",
};

const TRANSFORM_SKILL = {
  LEFT: "translate(0,0)",
  LEFT_BOTTOM: "translate(35px,-30px)",
  MIDDLE: "translate(0,0)",
  RIGHT_BOTTOM: "translate(-60px,-40px)",
  RIGHT: "translate(0,0)",
};

const Skill = ({ position, firstSkill, parentId }) => {
  useEffect(() => {
    console.log(position);
    switch (position) {
      case POSITION_SKILL.LEFT: {
        setTransformLine(TRANSFORM_LINE.LEFT);
        setTransformSkill(TRANSFORM_SKILL.LEFT);
        break;
      }
      case POSITION_SKILL.LEFT_BOTTOM: {
        setTransformLine(TRANSFORM_LINE.LEFT_BOTTOM);
        setTransformSkill(TRANSFORM_SKILL.LEFT_BOTTOM);
        break;
      }
      default:
      case POSITION_SKILL.MIDDLE: {
        setTransformLine(TRANSFORM_LINE.MIDDLE);
        setTransformSkill(TRANSFORM_SKILL.MIDDLE);
        break;
      }
      case POSITION_SKILL.RIGHT: {
        setTransformLine(TRANSFORM_LINE.RIGHT);
        setTransformSkill(TRANSFORM_SKILL.RIGHT);
        break;
      }
      case POSITION_SKILL.RIGHT_BOTTOM: {
        setTransformLine(TRANSFORM_LINE.RIGHT_BOTTOM);
        setTransformSkill(TRANSFORM_SKILL.RIGHT_BOTTOM);
        break;
      }
    }
  }, [position]);
  const [transformLine, setTransformLine] = useState(TRANSFORM_LINE.MIDDLE);
  const [transformSkill, setTransformSkill] = useState(TRANSFORM_SKILL.MIDDLE);

  const generateSkill = () => {
    let parentElement = document.getElementById(parentId);

    var childElement = document.createElement("div");
    childElement.className = "child";
    childElement.textContent = "Enfant " + (i + 1);

    var parentLeft = parentElement.offsetLeft;
    var parentBottom = parentElement.offsetTop + parentElement.offsetHeight;

    childElement.style.left = parentLeft + "px";
    childElement.style.top = parentBottom - childElement.offsetHeight + "px";

    parentElement.appendChild(childElement);
  };
  return (
    <>
      {generateSkill()}
    </>
  );
};

Skill.propTypes = {
  position: PropTypes.string,
  firstSkill: PropTypes.bool,
  parentId: PropTypes.string,
};

export default Skill;
