import React from "react";
import styled from "styled-components";
import PropTypes from "prop-types";
import { Col, Divider, Row } from "antd";

const BlocStyled = styled.div`
  position: absolute;
  top: 30px;
  left: -400px;
  background-color: white;
  width: 500px;
  height: 200px;
  font-size: 25px;
  border-radius: 5px;
`;

const DivLevel = styled.div`
  font-size: 50px;
  text-align: center;
  margin: 20px;
`;

const DivPoints = styled.div`
  font-size: 20px;
  text-align: center;
  margin: 2px;
`;

const DashBoard = ({
  remainingPoints,
  selectedSkills,
  level,
  simulationInProgress,
}) => {
  return (
    simulationInProgress && (
      <BlocStyled>
        <Row>
          <Col span={10}>
            <DivLevel>{`Niv. ${level}`}</DivLevel>
            <Divider style={{ margin: 0 }} />
            <DivPoints>
              <span style={{ fontWeight: 600 }}>{remainingPoints}</span> points
              restantes à dépenser
            </DivPoints>
          </Col>
          <Col>
            <div style={{ overflow: "auto", width: "288px", height: "97px" }}>
              <ul>
                {selectedSkills.map((item) => (
                  <li>{item.text}</li>
                ))}
              </ul>
            </div>
            <Divider style={{ margin: 0 }} />

            <div style={{ overflow: "auto", width: "288px", height: "97px" }}>
              <ul>
                {selectedSkills.map((item) => item.isSquare (
                  <li>{item.text}</li>
                ))}
              </ul>
            </div>
          </Col>
        </Row>
      </BlocStyled>
    )
  );
};

DashBoard.propTypes = {
  remainingPoints: PropTypes.number,
  selectedSkills: PropTypes.array,
  level: PropTypes.number,
  simulationInProgress: PropTypes.bool,
};

export default DashBoard;
